import PageContent from "../components/PageContent";
import { json, useRouteError } from "react-router-dom";

const ErrorPage = () => {
  const error = useRouteError();
  let message = "Something went wrong!";
  let title = "An error has occured!";

  if (JSON.parse(error.data).status === 500) {
    message = JSON.parse(error.data).message;
  }
  if (JSON.parse(error.data).status === 400) {
    message = "Could not found the source of page!";
    title = "Not found";
  }

  return (
    <PageContent title="An error has occured!">
      <p>An Error has occured!</p>
    </PageContent>
  );
};
export default ErrorPage;
